import React,{useState} from "react";
import axios from 'axios';
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import Zoom  from "@material-ui/core/Zoom";
function CreateArea(props) {

  const[input ,setinput]= useState({
    title:"",
    content:""

  });
  function handleclicked(event){
    const {name,value}=event.target;
    setinput(prevNote =>{
        return{
          ...prevNote,
          [name]:value
        };
    });


  }
  function submit(event){
    props.onAdd(input);
    event.preventDefault();
    setinput({title:"",content:""});
  }
   function handleSubmit (e){
    e.preventDefault();

    const {title, content } = this.state;

    const Note = {
      title,
      content,
    };

    axios
      .post('http://localhost:5000/notes', Note)
      .then(() => console.log('Note Created'))
      .catch(err => {
        console.error(err);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit} method="post"className="create-note">
        <input onChange={handleclicked} name="title" value={input.title} placeholder="Title" />
        <textarea name="content" onChange={handleclicked}
        value={input.content} placeholder="Take a note..." rows="3" />
       <Zoom in={true}><Fab onClick={submit}><AddIcon/></Fab></Zoom> 
      </form>
    </div>
  );
}

export default CreateArea;
