import React,{useState} from "react";
import axios from 'axios';
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import Zoom  from "@material-ui/core/Zoom";
function CreateArea(props) {

  const[title ,setTitle]= useState("");
  const[content ,setContent]= useState("");
  function handleclicked(event){
    const {name,value}=event.target;
    setTitle(prevNote =>{
        return{
          ...prevNote,
          [name]:value
        };
    });
    setContent(prevNote =>{
      return{
        ...prevNote,
        [name]:value
      };
  });


  }
  function submit(event){
    props.onAdd(title,content);
    event.preventDefault();
    const Note ={title,content};
    fetch('http://localhost:3000/notes',{
      method:'POST',
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify(Note)
    }).then(()=>{console.log('new note added');})
    setinput({title:"",content:""});
  }



  return (
    <div>
      <form  className="create-note">
        <input onChange={handleclicked} name="title" value={title} placeholder="Title" />
        <textarea name="content" onChange={handleclicked}
        value={content} placeholder="Take a note..." rows="3" />
       <Zoom in={true}><Fab onClick={submit}><AddIcon/></Fab></Zoom> 
      </form>
    </div>
  );
}

export default CreateArea;
